import android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport androidx.recyclerview.widget.LinearLayoutManagerimport com.erman.drawerfm.Rimport com.erman.drawerfm.adapters.DirectoryRecyclerViewAdapterimport kotlinx.android.synthetic.main.file_list_fragment.*class ListDirFragment : Fragment() {    private lateinit var mFilesAdapter: DirectoryRecyclerViewAdapter    private lateinit var PATH: String    companion object {        fun buildFragment(path: String): ListDirFragment {            val fragment = ListDirFragment()            val argumentBundle = Bundle()            argumentBundle.putString("path", path)            fragment.arguments = argumentBundle            return fragment        }    }    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.file_list_fragment, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        val filePath = arguments?.getString("path")        PATH = filePath.toString()        initViews()    }    private fun initViews() {        fileListRecyclerView.layoutManager = LinearLayoutManager(context)        mFilesAdapter = DirectoryRecyclerViewAdapter()        fileListRecyclerView.adapter = mFilesAdapter        updateDate()    }    private fun updateDate() {        val files = getDirectoryData(getFiles(PATH))        mFilesAdapter.updateData(files)    }}